#include "deviceid.h" /* NULL */

/* TODO: optimize these functions */

int get_vendor( int id ){
    int i;
    pci_device_id_t* dev;

    for ( i = 0; ; i++ ){
        dev = &pci_device_id_list[i];
        /* TODO: sizeof() */
        if( dev -> type == '\0' ) {
            break;
        }

        if( id == dev -> id && dev -> type == 'V' ) {
            return i;
        }
    }
    return -1;
}

const char* get_device_name( int id, int i ){
    pci_device_id_t* dev;

    for ( i++; ; i++ ){
        dev = &pci_device_id_list[i];
        /* TODO: sizeof() */
        if( dev -> type == 'V' || dev -> type == '\0' ) {
            break;
        }

        if( id == dev -> id && dev -> type == 'D' ) {
            return dev -> name;
        }
    }
    return NULL;
}

